mapper.insert:
 INSERT INTO MEMBER(   USERID 
					       , USERPASS 
					       , USERNAME 
					       , USERCOMPANY
					       , USERZIPCODE
					       , USERADDRESS
					       , USERSPHONE
					       , USERPHONE
					       , EMAIL)
               VALUES(    #{userId} 
              		   , #{userPass}
              		   , #{userName}
              		   , #{userCompany}
              		   , #{userZipcode}
              		   , #{userAddress}
              		   , #{userSphone}
              		   , #{userPhone}
              		   , #{email})

mapper.select:
SELECT USERID, USERPASS, USERNAME
  FROM MEMBER
 WHERE USERID = #{userId}

mapper.update:
UPDATE MEMBER SET 

USERNAME = #{userName}
WHERE USERID = #{userId}

mapper.select:
SELECT COUNT(*) FROM MEMBER
WHERE USERID = #{userId}
  AND USERPASS = #{userPass}
